            END IF;
         WHEN cl2 => 
            next_state <= cl3;
         WHEN cl3 => 
            next_state <= cl0;
         WHEN OTHERS =>
            next_state <= cl0;
      END CASE;
   END PROCESS nextstate_proc;
 
